use mock_server::{create_router, MockResponse, MockServerState};
use std::sync::Arc;

#[tokio::main]
async fn main() {
    let port = std::env::var("PORT")
        .unwrap_or_else(|_| "8666".to_string())
        .parse::<u16>()
        .expect("PORT must be a valid u16");

    let mock_response = Arc::new(MockResponse {
        status: axum::http::StatusCode::OK,
        body: RESP.to_string(),
        content_type: "text/x-component; charset=utf-8".to_string(),
    });

    let state = MockServerState {
        response: mock_response,
    };

    let app = create_router(state);

    let listener = tokio::net::TcpListener::bind(format!("0.0.0.0:{}", port))
        .await
        .expect("Failed to bind to address");

    println!("Mock server listening on http://0.0.0.0:{}", port);
    println!("Mock endpoint: http://0.0.0.0:{}/favorites", port);

    axum::serve(listener, app)
        .await
        .expect("Server error");
}

